package com.core.util;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

import java.io.*;

/**
 * <h3>GetPictureORBase64  Class</h3>
 *
 * @author : YuXiang
 * @date : 2019-09-16 16:28
 **/
@SuppressWarnings("restriction")
public class GetPictureORBase64 {
    /*将图片转码为base64字符串*/
    public static String GetImageStr(String imgFilePath) {
        // 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
        byte[] data = null;
        // 读取图片字节数组
        try {
            InputStream in = new FileInputStream(imgFilePath);
            data = new byte[in.available()];
            in.read(data);
            in.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        // 对字节数组Base64编码
        BASE64Encoder encoder = new BASE64Encoder();
        // 返回Base64编码过的字节数组字符串
        return encoder.encode(data);

    }

    /**
     * 将base64字符串转码为图片
     */
    public static boolean GenerateImage(String imgStr, String imgFilePath) {
        // 对字节数组字符串进行Base64解码并生成图片
        if (imgStr == null) {
            // 图像数据为空
            return false;
        } else {
            BASE64Decoder decoder = new BASE64Decoder();
            try {
                // Base64解码
                byte[] bytes = decoder.decodeBuffer(imgStr);
                for (int i = 0; i < bytes.length; ++i) {
                    if (bytes[i] < 0) {
                        // 调整异常数据
                        bytes[i] += 256;
                    }
                }
                // 生成jpg图片
                OutputStream out = new FileOutputStream(imgFilePath);
                out.write(bytes);
                out.flush();
                out.close();
                return true;
            } catch (Exception e) {
                return false;
            }
        }
    }

    public static void main(String[] args) {
        // 测试从Base64编码转换为图片文件
        String str = GetImageStr("C:\\Users\\Administrator\\Pictures\\微信图片_20190528095424.jpg");
        System.out.println(str);
        String strImg = "data:image/jpeg;base64,";
        File file = new File("image/");
        if (!file.exists()) {
            file.mkdir();
        }
        GenerateImage(str, "image/9b0bedaae26047cbacc0000abe267111.jpg");
        // // 测试从图片文件转换为Base64编码
//		System.out.println(GetImageStr("E:\\image\\wangyc.jpg"));
    }

}
